```kotlin
class PointOfServiceAdapter(private val clickListener: MyClickListener) :
    ListAdapter<PointsOfServiceWithTotalOrdersAndTotalPOS, PointOfServiceAdapter.PointsOfServiceViewHolder>(DiffCallback) {

    override fun onBindViewHolder(holder: PointsOfServiceViewHolder, position: Int) {
        val pointsOfServiceWithTotalOrdersAndTotalPOS = getItem(position)
