private fun filterPOSList(query: String?) {
    val filteredList = if (query.isNullOrEmpty()) {
        // If the search query is empty, show the entire list
        posViewModel.pointsOfService.value
    } else {
        // Filter the list based on the search query
        val filterPattern = query.toString().trim().toLowerCase(Locale.ROOT)
        posViewModel.pointsOfService.value?.filter {
            it.pointOfServiceName?.toLowerCase(Locale.ROOT)?.contains(filterPattern) == true
        }
    }

    // Update the adapter with the filtered list
    pointOfServiceAdapter.submitList(filteredList)
}
