            override fun onItemClick(myData: PointsOfService) {
                // Handle item click event
            }
        }, object : PointOfServiceAdapter.TotalPOSCallback {
            override fun onTotalPOSUpdated(totalPOS: Int) {
                // Handle total POS update
            }
        })
        binding.recyclerView.adapter = adapter

        // Fetch data from the Room table and filter it
        val filteredData = fetchAndFilterDataFromRoomTable()

        // Update the adapter with the filtered data
