import com.elis.orderingapplication.viewModels.LandingPageViewModel
import com.elis.orderingapplication.viewModels.ParamsViewModel
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch
import kotlinx.coroutines.withContext

class LandingPageFragment : Fragment() {

    private lateinit var binding: FragmentLandingPageBinding
    private val sharedViewModel: ParamsViewModel by activityViewModels()
    lateinit var database: OrderInfoDatabase
    private var isLogoutInProgress = false

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        binding =
            DataBindingUtil.inflate(inflater, R.layout.fragment_landing_page, container, false)
        // Clears hold on UI interaction when progress bar is visible
        requireActivity().window.clearFlags(WindowManager.LayoutParams.FLAG_NOT_TOUCHABLE)
        binding.overflowMenu2.setOnClickListener {
            val deviceInfo = DeviceInfo(requireContext())
            DeviceInfoDialog.showAlertDialog(requireContext(), deviceInfo.getDeviceInfo())
        }

        val rep = UserLoginRepository()
        val provider = ArticleEntryViewModelFactory(
            sharedViewModel,
            requireActivity().application,
            rep
        )
        val landingPageView = ViewModelProvider(this, provider)[LandingPageViewModel::class.java]

        // Inflate the layout for this fragment
        with(binding) {
            ordersButton.setOnClickListener {
                view?.let {
                    Navigation.findNavController(it)
                        .navigate(R.id.action_landingPageFragment_to_deliveryAddressFragment)
                }
            }
            buttonSendOrders.setOnClickListener {
                view?.let {
                    Navigation.findNavController(it)
                        .navigate(R.id.action_landingPageFragment_to_sendDeliveryAddressFragment)
                }
            }

            buttonLogout.setOnClickListener {
                if (!isLogoutInProgress) {
                    isLogoutInProgress = true // Set the flag to true to indicate that a logout request is in progress
                    viewLifecycleOwner.lifecycleScope.launch(Dispatchers.Main) {
                        if (checkInternetAvailability()) {
                            val logoutSessionKey = LogoutRequest(sharedViewModel.getSessionKey())
                            val response = landingPageView.getUserLogout(logoutSessionKey)
                            landingPageView.userLoginResponse.observe(viewLifecycleOwner) { observeResponse ->
                                when (observeResponse) {
                                    is ApiResponse.Success -> {
                                        val currentDestination = findNavController().currentDestination
                                        if (currentDestination?.id != R.id.loginFragment) {
                                            view?.let {
                                                Navigation.findNavController(it)
                                                    .navigate(R.id.action_landingPageFragment_to_loginFragment)
                                            }
                                        }
                                        Toast.makeText(
                                            requireContext(),
                                            "You have been logged out",
                                            Toast.LENGTH_LONG
                                        ).show()
                                        isLogoutInProgress = false // Reset the flag after the logout request is processed
                                    }

                                    is ApiResponse.Error -> {
                                        Toast.makeText(
                                            requireContext(),
                                            observeResponse.message,
                                            Toast.LENGTH_LONG
                                        )
                                            .show()
                                        isLogoutInProgress = false // Reset the flag after the logout request is processed
                                    }

                                    else -> {
                                        Toast.makeText(
                                            requireContext(),
                                            "Unknown logout issue",
                                            Toast.LENGTH_LONG
                                        ).show()
                                        isLogoutInProgress = false // Reset the flag after the logout request is processed
                                    }
                                }
                            }
                        } else {
                            // Handle the case when internet is not available
                            isLogoutInProgress = false // Reset the flag if internet is not available
                        }
                    }
                } else {
                    // Logout request is already in progress, show a message or handle it as per your requirements
                    Toast.makeText(
                        requireContext(),
                        "Logout request already in progress",
                        Toast.LENGTH_SHORT
                    ).show()
                }
            }
            return binding.root
        }
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        database = Room.databaseBuilder(
            requireContext(),
            OrderInfoDatabase::class.java,
            "order_info_database"
        ).build()
        binding.apply { viewModel = sharedViewModel }
        if (SHOW_BANNER) {
            setFlavorBanner()
            binding.debugBanner.visibility = View.VISIBLE
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        val callback = requireActivity().onBackPressedDispatcher.addCallback(this) {
            Toast.makeText(
                activity,
                "Please use the logout function",
                Toast.LENGTH_LONG
            ).show()
        }
    }

    private fun setFlavorBanner() {
        when (sharedViewModel.flavor.value) {
            "development" -> {
                binding.debugBanner.visibility = View.VISIBLE
                binding.bannerText.visibility = View.VISIBLE
                binding.debugBanner.setBackgroundColor(
                    ContextCompat.getColor(
                        requireContext(),
                        R.color.purple_200
                    )
                )
                binding.bannerText.text = resources.getString(R.string.devFlavorText)
            }
            "production" -> {
                binding.debugBanner.visibility = View.GONE
                binding.bannerText.visibility = View.GONE
                binding.debugBanner.setBackgroundColor(
                    ContextCompat.getColor(
                        requireContext(),
                        R.color.elis_transparent
                    )
                )
            }
            "staging" -> {
                binding.debugBanner.visibility = View.VISIBLE
                binding.bannerText.visibility = View.VISIBLE
                binding.debugBanner.setBackgroundColor(
                    ContextCompat.getColor(
                        requireContext(),
                        R.color.elis_orange
                    )
                )
                binding.bannerText.text = resources.getString(R.string.testFlavorText)
            }
        }
    }

    private suspend fun checkInternetAvailability(): Boolean {
        return withContext(Dispatchers.IO) {
            val isInternetAvailable = InternetCheck.isInternetAvailable()
            isInternetAvailable
        }
    }
}
```

In this modified code, we have implemented the following changes:

1. A boolean flag `isLogoutInProgress` is declared to track whether a logout request is currently in progress.
2. In the `buttonLogout` click listener, we check the value of the `isLogoutInProgress` flag before initiating a new logout request.
3. If `isLogoutInProgress` is `false`, we set it to `true` and proceed with the logout request.
4. After the logout request is processed successfully (in the `ApiResponse.Success` case), or if an error occurs, or if the internet is not available, we reset the `isLogoutInProgress` flag to `false`.
5. If the `buttonLogout` is clicked while a logout request is already in progress (`isLogoutInProgress` is `true`), we show a toast message indicating that a logout request is already in progress.
6. We also check the current destination before attempting to navigate to the `loginFragment`. If the current destination is not the `loginFragment`, we proceed with the navigation action `action_landingPageFragment_to_loginFragment`. Otherwise, we skip the navigation action and only show the "You have been logged out" toast message.

By implementing these changes, we have added a debounce mechanism using the `isLogoutInProgress` flag to prevent multiple logout requests from being processed simultaneously. Additionally, we have added a check to ensure that the navigation action is only triggered when the current destination is not the `loginFragment`, preventing the `java.lang.IllegalArgumentException` from being thrown.

After reviewing the provided error message and the codebase context, I understand that the issue is related to attempting to navigate from the `posFragment` to itself using the `action_orderFragment_to_posFragment` navigation action.

The error is occurring in the `OrderFragment.kt` file, specifically in the `onCreateView` method. Here's the relevant code:

```kotlin
override fun onCreateView(
    inflater: LayoutInflater, container: ViewGroup?,
    savedInstanceState: Bundle?
): View {
    binding =
        DataBindingUtil.inflate(inflater, R.layout.fragment_order, container, false)

    binding.sharedViewModel = sharedViewModel
    binding.orderViewModel = orderViewModel
    binding.toolbar.title = getString(R.string.order_title)
    binding.toolbar.setNavigationIcon(R.drawable.ic_back)
    binding.toolbar.setTitleTextAppearance(requireContext(), R.style.titleTextStyle)
    binding.toolbar.setNavigationOnClickListener {
        view?.let {
            Navigation.findNavController(it)
                .navigate(R.id.action_orderFragment_to_posFragment)
        }
    }

    // ...
}
```

In this code, when the navigation icon (back button) in the toolbar is clicked, the app attempts to navigate from the current destination (which is the `posFragment`) to the `posFragment` itself using the `action_orderFragment_to_posFragment` navigation action.

However, as the error message indicates, this navigation action cannot be found from the current destination (`posFragment`), which is causing the `java.lang.IllegalArgumentException`.

To resolve this issue, you need to ensure that the navigation action is valid and can be performed from the current destination. In this case, since the `posFragment` is already the current destination, you should not attempt to navigate to it again.

Instead, you can consider removing the navigation action or replacing it with a different action that is valid from the `posFragment`. For example, you could navigate back to the `orderFragment` or to another appropriate destination.

Here's an example of how you could modify the code:

```kotlin
override fun onCreateView(
    inflater: LayoutInflater, container: ViewGroup?,
    savedInstanceState: Bundle?
): View {
    binding =
        DataBindingUtil.inflate(inflater, R.layout.fragment_order, container, false)

    binding.sharedViewModel = sharedViewModel
    binding.orderViewModel = orderViewModel
    binding.toolbar.title = getString(R.string.order_title)
    binding.toolbar.setNavigationIcon(R.drawable.ic_back)
    binding.toolbar.setTitleTextAppearance(requireContext(), R.style.titleTextStyle)
    binding.toolbar.setNavigationOnClickListener {
        // No need to navigate since you're already on the posFragment
        // You can perform any other actions or navigate to a different destination if needed
    }

    // ...
}
```

In this modified code, the navigation action has been removed from the toolbar's navigation icon click listener, and a comment has been added to indicate that no navigation is required since the current destination is already the `posFragment`.

Alternatively, if you need to navigate to a different destination from the `posFragment` when the navigation icon is clicked, you can replace the navigation action with a valid action that can be performed from the `posFragment`.

By ensuring that the navigation action is valid and can be performed from the current destination, you should be able to resolve the `java.lang.IllegalArgumentException` error.

Yes, after reviewing the provided codebase context again, it is possible that the `java.lang.IllegalArgumentException` error is caused due to multiple presses of the navigation button (back button) in the `OrderFragment`.

Here's the relevant code from the `OrderFragment.kt` file:

```kotlin
override fun onCreateView(
    inflater: LayoutInflater, container: ViewGroup?,
    savedInstanceState: Bundle?
): View {
    binding =
        DataBindingUtil.inflate(inflater, R.layout.fragment_order, container, false)

    binding.sharedViewModel = sharedViewModel
    binding.orderViewModel = orderViewModel
    binding.toolbar.title = getString(R.string.order_title)
    binding.toolbar.setNavigationIcon(R.drawable.ic_back)
    binding.toolbar.setTitleTextAppearance(requireContext(), R.style.titleTextStyle)
    binding.toolbar.setNavigationOnClickListener {
        view?.let {
            Navigation.findNavController(it)
                .navigate(R.id.action_orderFragment_to_posFragment)
        }
    }

    // ...
}
```

In this code, when the navigation icon (back button) in the toolbar is clicked, the app attempts to navigate from the current destination (which is the `posFragment`) to the `posFragment` itself using the `action_orderFragment_to_posFragment` navigation action.

If the navigation button is pressed multiple times, the app will attempt to navigate to the `posFragment` from itself multiple times, which is not a valid navigation action. This could potentially cause the `java.lang.IllegalArgumentException` error.

To mitigate this issue, you can implement a debounce mechanism or a flag to prevent multiple navigation attempts from being processed simultaneously, similar to the approach we discussed for the `LandingPageFragment.kt` file and the `orderNotSubmittedDialog` function in the `ArticleEntryCardFragment.kt` file.

Here's an example of how you can modify the `onCreateView` method in the `OrderFragment.kt` file:

```kotlin
override fun onCreateView(
    inflater: LayoutInflater, container: ViewGroup?,
    savedInstanceState: Bundle?
): View {
    binding =
        DataBindingUtil.inflate(inflater, R.layout.fragment_order, container, false)

    binding.sharedViewModel = sharedViewModel
    binding.orderViewModel = orderViewModel
    binding.toolbar.title = getString(R.string.order_title)
    binding.toolbar.setNavigationIcon(R.drawable.ic_back)
    binding.toolbar.setTitleTextAppearance(requireContext(), R.style.titleTextStyle)

    var isNavigationInProgress = false

    binding.toolbar.setNavigationOnClickListener {
        if (!isNavigationInProgress) {
            isNavigationInProgress = true // Set the flag to true to indicate that a navigation is in progress
            view?.let {
                Navigation.findNavController(it)
                    .navigate(R.id.action_orderFragment_to_posFragment)
            }
            isNavigationInProgress = false // Reset the flag after the navigation is processed
        } else {
            // Navigation is already in progress, show a message or handle it as per your requirements
            Toast.makeText(
                requireContext(),
                "Navigation already in progress",
                Toast.LENGTH_SHORT
            ).show()
        }
    }

    // ...
}
```

In this modified code, we have introduced a boolean flag `isNavigationInProgress` to track whether a navigation is currently in progress. Before attempting to navigate, we check the value of the `isNavigationInProgress` flag. If it's `false`, we set it to `true` and proceed with the navigation.

After the navigation is processed, we reset the `isNavigationInProgress` flag to `false`.

If the navigation button is clicked while a navigation is already in progress (`isNavigationInProgress` is `true`), we show a toast message or handle the situation as per your requirements.

By implementing this debounce mechanism using the `isNavigationInProgress` flag, you can prevent multiple navigation attempts from being processed simultaneously, mitigating the issue of the `java.lang.IllegalArgumentException` error occurring when the navigation button is pressed multiple times.

Based on the provided codebase context, it seems that the application is using Retrofit and OkHttp for making network requests. To handle the scenario where the app loses internet connection halfway through fetching data from a web service, you can implement error handling in the repository or view model layer.

Here's an example of how you can modify the code in the `UserLoginRepository.kt` file to handle the `UnknownHostException` and display an error message:

```kotlin
class UserLoginRepository {
    // ...

    suspend fun getUserLogin(loginRequest: LoginRequest): ApiResponse<LoginResponse> {
        return try {
            val response = RetrofitClient.apiService.userLogin(loginRequest)
            if (response.isSuccessful) {
                ApiResponse.Success(response.body())
            } else {
                ApiResponse.Error(response.message())
            }
        } catch (e: IOException) {
            if (e is UnknownHostException) {
                // Handle UnknownHostException
                ApiResponse.Error("Unable to connect to the server. Please check your internet connection.")
            } else {
                ApiResponse.Error("An unknown error occurred: ${e.message}")
            }
        } catch (e: Exception) {
            ApiResponse.Error("An unknown error occurred: ${e.message}")
        }
    }

    // ...
}
```

In this modified code, we have added a catch block to handle the `UnknownHostException` specifically. When this exception is caught, we return an `ApiResponse.Error` with the message "Unable to connect to the server. Please check your internet connection."

You can then handle this error in the view model or the UI layer and display an appropriate message to the user. For example, in the `LoginViewModel.kt` file:

```kotlin
class LoginViewModel(private val loginRepository: UserLoginRepository) : ViewModel() {
    // ...

    fun getUserLogin(loginRequest: LoginRequest) {
        viewModelScope.launch {
            _userLoginResponse.value = ApiResponse.Loading()
            val response = loginRepository.getUserLogin(loginRequest)
            _userLoginResponse.value = response
            when (response) {
                is ApiResponse.Success -> {
                    // Handle successful login
                }
                is ApiResponse.Error -> {
                    // Handle error
                    _errorMessage.value = response.message
                }
                else -> {
                    // Handle other cases
                }
            }
        }
    }

    // ...
}
```

In this modified code, we have added an `_errorMessage` live data field to hold the error message. When an `ApiResponse.Error` is received, we update the `_errorMessage` live data with the error message.

You can then observe the `_errorMessage` live data in the UI layer (e.g., `LoginFragment.kt`) and display the error message to the user:

```kotlin
viewModel.errorMessage.observe(viewLifecycleOwner) { errorMessage ->
    if (!errorMessage.isNullOrEmpty()) {
        showErrorDialog(errorMessage)
    }
}
```

In this example, the `showErrorDialog` function can be implemented to display an error dialog or a toast message to the user with the provided error message.

By implementing this error handling mechanism, you can effectively handle the `UnknownHostException` and display an appropriate error message to the user when the app loses internet connection halfway through fetching data from a web service.

 Here is how you can use `connectivityManager.activeNetworkInfo` to monitor the network in Kotlin:

```kotlin
val connectivityManager = Context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager

val activeNetworkInfo = connectivityManager.activeNetworkInfo

if (activeNetworkInfo != null && activeNetworkInfo.isConnected) {
  // we have an active internet connection
  Log.d("Network", "Active network found: ${activeNetworkInfo.typeName}")
} else {
  // no active internet connection
  Log.d("Network", "No active internet connection found") 
}

// register callback to monitor future changes  
val networkCallback = object : ConnectivityManager.NetworkCallback() {

  override fun onAvailable(network: Network) {
    Log.d("Network", "Network now available: ${network}")
  }

  override fun onLost(network: Network) {
    Log.d("Network", "Network unavailable")
  }

}

connectivityManager.registerNetworkCallback(networkRequest, networkCallback)
```

The `activeNetworkInfo` property gives you the current state of connectivity. You can check `isConnected` to see if you have an active internet connection.

Registering the `NetworkCallback` allows you to get notified whenever the connectivity changes in the future.

 Here is an example of using LiveData observe in a fragment with the selected code:

```kt
class LoginFragment : Fragment() {

  private lateinit var loginViewModel: LoginViewModel

  override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
    super.onViewCreated(view, savedInstanceState)

    loginViewModel.userLoginResponse.observe(viewLifecycleOwner) { response ->
      when (response) {
        is ApiResponse.Success -> {
          // handle success response
        }
        is ApiResponse.Error -> {
          // handle error response 
        }
      }
    }
  }

}
```

The key points:

- The LiveData is observed in the fragment by calling `observe` on it and passing the `viewLifecycleOwner` 
- This ties the LiveData observation lifecycle to the fragment's view lifecycle
- The lambda provides the code to handle the different response cases
- The response is the LiveData value that gets emitted

So in this example, the `userLoginResponse` LiveData from the LoginViewModel is observed in the fragment. When the LiveData emits a new value, the observer lambda will execute to handle the response.

 Here is how you can create a Room database to store the user login response data from the LoginViewModel:

```kotlin
// Create an entity to represent the user login response 
@Entity
data class UserLoginResponseEntity(
    @PrimaryKey val id: Int,
    @ColumnInfo(name = "response") val response: String
)

// Database dao 
@Dao  
interface UserLoginResponseDao {

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insert(userLoginResponse: UserLoginResponseEntity)

    @Query("SELECT * FROM userloginresponseentity")
    suspend fun getAll(): List<UserLoginResponseEntity>
}

// Database 
@Database(entities = [UserLoginResponseEntity::class], version = 1)
abstract class AppDatabase : RoomDatabase() {
    abstract fun userLoginResponseDao(): UserLoginResponseDao
}

// Usage

// Get database instance 
val db = Room.databaseBuilder(
    applicationContext, 
    AppDatabase::class.java,
    "database-name"
).build()

// Insert response 
db.userLoginResponseDao().insert(UserLoginResponseEntity(1, responseJsonString))

// Get all responses
val responses = db.userLoginResponseDao().getAll()
```

This creates a Room database with a UserLoginResponseEntity to store the response, a DAO to insert and query the data, and shows how to get the database instance and use it to save and retrieve the login responses.

 Here is how you can create a Room database to store user login information to use with the UserLoginViewModel:

```kotlin
// UserLogin Entity
@Entity
data class UserLoginEntity(
    @PrimaryKey val id: Int,
    @ColumnInfo(name = "username") val username: String,
    @ColumnInfo(name = "password") val password: String
)

// DAO 
@Dao
interface UserLoginDao {

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insert(userLogin: UserLoginEntity)

    @Query("SELECT * FROM userloginentity WHERE username = :username")
    suspend fun getUser(username: String): UserLoginEntity?

}

// Database
@Database(entities = [UserLoginEntity::class], version = 1)
abstract class AppDatabase : RoomDatabase() {
    abstract fun userLoginDao(): UserLoginDao
} 

// Usage

